import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import CssBaseline from '@material-ui/core/CssBaseline';
import Button from '@material-ui/core/Button';
import Link from '@material-ui/core/Link';
import SimpleCard from './SimpleCard';

const covidData = [];
let count = 10;
fetch('https://covidtracking.com/api/v1/us/daily.json')
    .then(response => response.json())
    .then((data) => {
      for (let i = 0; i < 10; i++) {
        covidData[i] = data;
      }
      /*data.forEach((i) => {
        if(count > 0){
        //console.log("This is one ", i);
        covidData.push({
          death: i.death,
          hospitalized: i.hospitalized,
          negative: i.negative,
          positive: i.positive,
          pending: i.pending
        })
        count --
        }
      })*/;
console.log(covidData)
    });

const listItems = covidData.map((data) =>
        <Grid item xs={3}>
          <SimpleCard death={data.death} positive={data.positve} pending={data.pending} hospitalizedCurrently= {data.hospitalized}/>
        </Grid>
    );
  /*const listItems = fetch('https://covidtracking.com/api/v1/us/daily.json')
          .then(response => response.json())
          .then((data) => {
            data.forEach((i) =>{<Grid item xs={3}><SimpleCard death={i.death}/></Grid>}
            );
          })
          */
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 140,
  },
  lpaper: {
    padding: theme.spacing(20),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 500,
  },
  mpaper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 200,
  },
  footer: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 75,
  }
}));

function DataT(props) {
  const classes = useStyles();

  return (
    <React.Fragment>
    <CssBaseline>
    <div className={classes.root}>
      <Grid container spacing={3}>
        {/* Main */}
        <Grid item xs={12}>
          <Paper className={classes.lpaper}
          style={{ backgroundImage: 'url(https://source.unsplash.com/user/erondu/1600x900)',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat'}}>
            Large space
          </Paper>
        </Grid>
        {/* Grid With Cards or Paper */}
        <Grid item xs={6}>
          <Paper className={classes.paper}>
            Results for {props.stateVal}
          </Paper>
        </Grid>
        {/* Grid With Cards or Paper */}
        <Grid item xs={6}>
          <Paper className={classes.paper}>
            Optional
          </Paper>
        </Grid>
        {/* Grid With Cards or Paper */}
        {covidData.forEach((data) =>
          <Grid item xs={3}>
            <SimpleCard death={data.death} positive={data.positve} pending={data.pending} hospitalizedCurrently= {data.hospitalized}/>
          </Grid>
        )}
        <Grid item xs={3}>
            <SimpleCard death={covidData.death}/>
        </Grid>
        {/* Footer */}
        <Grid item xs={12}>
          <Paper className={classes.footer}>
            Footer
          </Paper>
        </Grid>
      </Grid>
    </div>
    </CssBaseline>
    </React.Fragment>
  );
}

export default DataT;
